import React, { useState, useEffect } from 'react';
import {useLocation} from "react-router-dom";
import axios from 'axios';
let firstName

function useQuery() {
    return new URLSearchParams(useLocation().search);
}

const apiUrl = 'http://192.168.1.5:3000/api/person'

export default function Edit() {

    const [personDetail, setPersonDetail] = useState({})
    let query = useQuery();
    useEffect(() => {
        const id = query.get("id")
        axios.get(`${apiUrl}/${id}`)
        .then(res => {
            const details = res.data
            firstName = details.username
            setPersonDetail(details)
        })
    }, [])

    return (
        <>
            <form className='pt-4'>
                <div class="row">
                    <div class="col">
                        <input type="text" class="form-control" name='firstName' onChange={(e) => setPersonDetail({username: e.target.value})} value={personDetail.username} required />
                    </div>
                    <div class="col">
                        <input type="text" class="form-control" placeholder="Last name" name='lastName' value={firstName} required/>
                    </div>
                </div>
                <div class="row mt-3">
                    <div class="col">
                        <input type="number" class="form-control" placeholder="Age" name='age' value={personDetail.age} required />
                    </div>
                    <div class="col">
                        <input type="email" class="form-control" placeholder="Email" name='email' value={personDetail.email} required />
                    </div>
                </div>
                <div className='text-center pt-3'>
                    <input type="submit" className='btn btn-primary' value="Edit Person"/>
                </div>
            </form>
        </>
    )
}
