import React, { useState, useEffect } from 'react';
import {useLocation} from "react-router-dom";
import axios from 'axios';
let firstName

function useQuery() {
    return new URLSearchParams(useLocation().search);
}

const apiUrl = 'http://192.168.1.5:3000/api/person'

export default function Edit() {

    const [personDetail, setPersonDetail] = useState({})
    let query = useQuery();
    useEffect(() => {
        const id = query.get("id")
        axios.get(`${apiUrl}/${id}`)
        .then(res => {
            const details = res.data
            firstName = details.username
            setPersonDetail(details)
        })
    }, [])

    const updatePerson = () => {
        console.log('test')
        const {firstName, lastName, age, email} = personDetail
        axios.post(apiUrl, {
        firstName,
        lastName,
        age,
        email
        }).then(res => {
            alert('Edited')
        })
        .catch(err => alert('Unable to Add Person. Make sure you are using unique email'))
    }

    return (
        <>
            <form className='pt-4'>
                <div class="row">
                    <div class="col">
                        <input type="text" class="form-control" name='firstName' onChange={(e) => setPersonDetail({firstName: e.target.value})} value={personDetail.firstName} required />
                    </div>
                    <div class="col">
                        <input type="text" class="form-control" placeholder="Last name" name='lastName' onChange={(e) => setPersonDetail({lastName: e.target.value})} value={personDetail.lastName} required/>
                    </div>
                </div>
                <div class="row mt-3">
                    <div class="col">
                        <input type="number" class="form-control" placeholder="Age" name='age' onChange={(e) => setPersonDetail({age: e.target.value})} value={personDetail.age} required />
                    </div>
                    <div class="col">
                        <input type="email" class="form-control" placeholder="Email" name='email' onChange={(e) => setPersonDetail({email: e.target.value})} value={personDetail.email} required />
                    </div>
                </div>
                <div className='text-center pt-3'>
                    <input type="button" onClick={updatePerson} className='btn btn-primary' value="Edit Person"/>
                </div>
            </form>
        </>
    )
}
