import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from 'axios';

import List from "./components/List";
import Addperson from "./components/Addperson";

import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// const apiUrl = 'http://localhost:3000/api/person'

function App() {
  const [persons, setPersons] = useState([])

  useEffect(() => {
    axios.get(`https://jsonplaceholder.typicode.com/users`)
      .then(res => {
        const persons = res.data;
        setPersons(persons);
      })
  },[])

  const deletePerson = (id) => {
    axios.delete(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then(res => {
        const personsClone = [...persons]
        const newPersonList = personsClone.filter(person => {
          return person.id !== id
        })
        setPersons(newPersonList);
      }).catch(err => alert('Unable to remove todo'))
  }

  const addPerson = (e) => {
    e.preventDefault()
    const firstName = e.target.elements.firstName.value,
          lastName = e.target.elements.lastName.value,
          age = e.target.elements.age.value,
          email = e.target.elements.email.value

    console.log(firstName, lastName, age, email)
    axios.post('https://jsonplaceholder.typicode.com/todos', {
      title: 'test',
      completed: false
      // firstName,
      // lastName,
      // age,
      // email
    }).then(res => {
      const personsClone = [...persons]
      setPersons([...personsClone, res.data])
      // this.setState({ todos: [...this.state.todos, res.data]})
    })
      .catch(err => alert('Unable to add todo'))
  }

  return (
    <>
      <Router>
        <div className="container">
        <Route exact path='/' render={props => (
          <List users={persons} deleteUser={deletePerson}/>  
        )} />
        <Route path='/add' component={Addperson} />
        </div>
      </Router>
      <Addperson addUser={addPerson}/>

    </>
  );
}

export default App;
