import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from "react-router-dom";
let firstName
export default function Edit() {

    const [personDetail, setPersonDetail] = useState({})

    useEffect(() => {
        let { idx } = useParams();
        function getParameterByName(name, url = window.location.href) {
            name = name.replace(/[\[\]]/g, '\\$&');
            var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
                results = regex.exec(url);
            if (!results) return null;
            if (!results[2]) return '';
            return decodeURIComponent(results[2].replace(/\+/g, ' '));
        }
        const id = getParameterByName('id');
        axios.get(`https://jsonplaceholder.typicode.com/users/${id}`)
        .then(res => {
            const details = res.data
            firstName = details.username
            setPersonDetail(details)
        })
    }, [])

    return (
        <>
            <form>
                <div class="row">
                    <div class="col">
                        <input type="text" class="form-control" name='firstName' onChange={(e) => setPersonDetail({username: e.target.value})} value={personDetail.username} required />
                    </div>
                    <div class="col">
                        <input type="text" class="form-control" placeholder="Last name" name='lastName' value={firstName} required/>
                    </div>
                </div>
                <div class="row mt-3">
                    <div class="col">
                        <input type="number" class="form-control" placeholder="Age" name='age' value={personDetail.id} required />
                    </div>
                    <div class="col">
                        <input type="email" class="form-control" placeholder="Email" name='email' value={personDetail.email} required />
                    </div>
                </div>
                <div className='text-center pt-3'>
                    <input type="submit" value="Edit Person"/>
                </div>
            </form>
        </>
    )
}
