import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from "react-router-dom";
let firstName
export default function Edit() {

    const [personDetail, setPersonDetail] = useState({})

    useEffect(() => {

        function getParameterByName(name, url = window.location.href) {
            name = name.replace(/[\[\]]/g, '\\$&');
            var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
                results = regex.exec(url);
            if (!results) return null;
            if (!results[2]) return '';
            return decodeURIComponent(results[2].replace(/\+/g, ' '));
        }
        const idx = getParameterByName('id');
        axios.get(`https://jsonplaceholder.typicode.com/users/${idx}`)
        .then(res => {
            const details = res.data
            firstName = details.username
            setPersonDetail(details)
        })
    }, [])
    // let { id } = useParams();
    // console.log('test')
    return (
        <>
            <form>
                <div className="row">
                    <div className="col">
                        <input type="text" className="form-control" name='firstName' onChange={(e) => setPersonDetail({username: e.target.value})} value={personDetail.username} required />
                    </div>
                    <div className="col">
                        <input type="text" className="form-control" placeholder="Last name" name='lastName' value={firstName} required/>
                    </div>
                </div>
                <div className="row mt-3">
                    <div className="col">
                        <input type="number" className="form-control" placeholder="Age" name='age' value={personDetail.id} required />
                    </div>
                    <div className="col">
                        <input type="email" className="form-control" placeholder="Email" name='email' value={personDetail.email} required />
                    </div>
                </div>
                <div className='text-center pt-3'>
                    <input type="submit" value="Edit Person"/>
                </div>
            </form>
        </>
    )
}
