import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from 'axios';

import List from "./components/List";
import Addperson from "./components/Addperson";
import View from "./components/View";
import Edit from "./components/Edit";

import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const apiUrl = 'http://192.168.1.5:3000/api/person'

function App() {
  const [persons, setPersons] = useState([])

  useEffect(() => {
    axios.get(apiUrl)
      .then(res => {
        const persons = res.data;
        console.log(persons)
        setPersons(persons);
      })
  },[])

  const deletePerson = (id) => {
    axios.delete(`${apiUrl}/${id}`)
      .then(res => {
        const personsClone = [...persons]
        const newPersonList = personsClone.filter(person => {
          return person._id !== id
        })
        setPersons(newPersonList);
      }).catch(err => alert('Unable to Delete'))
  }

  const addPerson = (e) => {
    e.preventDefault()
    const firstName = e.target.elements.firstName.value,
          lastName = e.target.elements.lastName.value,
          age = e.target.elements.age.value,
          email = e.target.elements.email.value

    console.log(firstName, lastName, age, email)
    axios.post(apiUrl, {
      firstName,
      lastName,
      age,
      email
    }).then(res => {
      const personsClone = [...persons]
      setPersons([...personsClone, res.data])
    })
      .catch(err => alert('Unable to Add Person'))
  }

  const openEdit = (id) => {
    // e.preventDefault()   
    console.log(id)
  }

  return (
    <>
      <Router>
        <div className="container">
        <Route exact path='/' render={() => (
          <List users={persons} deleteUser={deletePerson} />  
        )} />
        <Route path='/add' render={() => (
          <Addperson addUser={addPerson}/>
        )} />
        <Route path='/view' render={() => (
          <View />
        )} />
        <Route path='/edit' render={() => (
          <Edit />
        )} />
        </div>
      </Router>
    </>
  );
}

export default App;
